import { useEffect, useState } from 'react';
import api from '../services/api';
import '../styles/dashboard.css';

function Dashboard() {
  const [tarefas, setTarefas] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [descricao, setDescricao] = useState('');
  const [editandoId, setEditandoId] = useState(null);
  const token = localStorage.getItem('token');

  const buscarTarefas = async () => {
    try {
      const res = await api.get('/tarefas', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTarefas(res.data);
    } catch (err) {
      alert('Erro ao buscar tarefas');
    }
  };

  const criarOuEditarTarefa = async () => {
    if (!titulo.trim() || !descricao.trim()) return;

    try {
      if (editandoId) {
        await api.put(
          `/tarefas/${editandoId}`,
          { descricao: `${titulo} || ${descricao}` },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setEditandoId(null);
      } else {
        await api.post(
          '/tarefas',
          { descricao: `${titulo} || ${descricao}`, status: 'pendente' },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }
      setTitulo('');
      setDescricao('');
      buscarTarefas();
    } catch (err) {
      alert('Erro ao salvar tarefa');
    }
  };

  const prepararEdicao = (tarefa) => {
    const [titulo, descricao] = tarefa.descricao.split(' || ');
    setTitulo(titulo);
    setDescricao(descricao || '');
    setEditandoId(tarefa._id);
  };

  const deletarTarefa = async (id) => {
    try {
      await api.delete(`/tarefas/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      buscarTarefas();
    } catch (err) {
      alert('Erro ao deletar tarefa');
    }
  };

  const proximoStatus = (status) => {
    if (status === 'pendente') return 'andamento';
    if (status === 'andamento') return 'concluido';
    return null;
  };

  const avancarTarefa = async (id, statusAtual) => {
    const novoStatus = proximoStatus(statusAtual);
    if (!novoStatus) return;

    try {
      await api.put(
        `/tarefas/${id}`,
        { status: novoStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      buscarTarefas();
    } catch (err) {
      alert('Erro ao atualizar status');
    }
  };

  useEffect(() => {
    buscarTarefas();
  }, []);

  const renderColuna = (titulo, status) => (
    <div className="kanban-coluna">
      <h3>{titulo}</h3>
      {tarefas
        .filter((t) => t.status === status)
        .map((tarefa) => {
          const [titulo, descricao] = tarefa.descricao.split(' || ');
          return (
            <div key={tarefa._id} className="kanban-card">
              <strong>{titulo}</strong>
              <p>{descricao}</p>
              <div className="acoes">
                {status !== 'concluido' && (
                  <button onClick={() => avancarTarefa(tarefa._id, status)}>Avançar</button>
                )}
                <button onClick={() => prepararEdicao(tarefa)}>Editar</button>
                <button onClick={() => deletarTarefa(tarefa._id)}>Excluir</button>
              </div>
            </div>
          );
        })}
    </div>
  );

  return (
    <div className="dashboard-kanban">
      <h2>{editandoId ? 'Editar Tarefa' : 'Nova Tarefa'}</h2>
      <div className="criar-tarefa">
        <label>
          Título:
          <input
            type="text"
            placeholder="Título"
            value={titulo}
            onChange={(e) => setTitulo(e.target.value)}
          />
        </label>
        <label>
          Descrição:
          <textarea
            placeholder="Descrição"
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
          />
        </label>
        <button onClick={criarOuEditarTarefa}>
          {editandoId ? 'Salvar Alterações' : 'Criar Tarefa'}
        </button>
      </div>

      <div className="kanban-board">
        {renderColuna('Pendente', 'pendente')}
        {renderColuna('Em Andamento', 'andamento')}
        {renderColuna('Concluído', 'concluido')}
      </div>
    </div>
  );
}

export default Dashboard;
