import { useState, useEffect } from 'react';
import api from '../services/api';
import '../styles/dashboard.css';

function Dashboard() {
  const [tarefas, setTarefas] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [filtro, setFiltro] = useState('todas');

  const token = localStorage.getItem('token');

  const buscarTarefas = async () => {
    try {
      const res = await api.get('/tarefas', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTarefas(res.data);
    } catch (err) {
      alert('Erro ao carregar tarefas');
    }
  };

  const criarTarefa = async (e) => {
    e.preventDefault();
    try {
      await api.post(
        '/tarefas',
        { titulo },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTitulo('');
      buscarTarefas();
    } catch (err) {
      alert('Erro ao criar tarefa');
    }
  };

  const concluirTarefa = async (id) => {
    await api.put(`/tarefas/${id}/concluir`, {}, { headers: { Authorization: `Bearer ${token}` } });
    buscarTarefas();
  };

  const excluirTarefa = async (id) => {
    await api.delete(`/tarefas/${id}`, { headers: { Authorization: `Bearer ${token}` } });
    buscarTarefas();
  };

  const tarefasFiltradas = tarefas.filter(tarefa => {
    if (filtro === 'concluidas') return tarefa.concluida;
    if (filtro === 'pendentes') return !tarefa.concluida;
    return true;
  });

  useEffect(() => {
    buscarTarefas();
  }, []);

  return (
    <div className="dashboard-container">
      <h2>Minhas Tarefas</h2>

      <form onSubmit={criarTarefa}>
        <input
          type="text"
          placeholder="Nova tarefa"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
          required
        />
        <button type="submit">Criar</button>
      </form>

      <div className="filtros">
        <button onClick={() => setFiltro('todas')}>Todas</button>
        <button onClick={() => setFiltro('concluidas')}>Conclu√≠das</button>
        <button onClick={() => setFiltro('pendentes')}>Pendentes</button>
      </div>

      <ul className="lista-tarefas">
        {tarefasFiltradas.map((tarefa) => (
          <li key={tarefa._id} className={tarefa.concluida ? 'concluida' : ''}>
            {tarefa.titulo}
            <div>
              {!tarefa.concluida && (
                <button onClick={() => concluirTarefa(tarefa._id)}>‚úîÔ∏è</button>
              )}
              <button onClick={() => excluirTarefa(tarefa._id)}>üóëÔ∏è</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
