import { useEffect, useState } from 'react';
import api from '../services/api';
import '../styles/dashboard.css';

function Dashboard() {
  const [tarefas, setTarefas] = useState([]);
  const [descricao, setDescricao] = useState('');

  const token = localStorage.getItem('token');

  const buscarTarefas = async () => {
    try {
      const res = await api.get('/tarefas', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTarefas(res.data);
    } catch (err) {
      alert('Erro ao buscar tarefas');
    }
  };

  const criarTarefa = async () => {
    if (!descricao.trim()) return;
    try {
      await api.post(
        '/tarefas',
        { descricao, status: 'pendente' },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setDescricao('');
      buscarTarefas();
    } catch (err) {
      alert('Erro ao criar tarefa');
    }
  };

  const atualizarStatus = async (id, novoStatus) => {
    try {
      await api.put(
        `/tarefas/${id}`,
        { status: novoStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      buscarTarefas();
    } catch (err) {
      alert('Erro ao atualizar status');
    }
  };

  const deletarTarefa = async (id) => {
    try {
      await api.delete(`/tarefas/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      buscarTarefas();
    } catch (err) {
      alert('Erro ao deletar tarefa');
    }
  };

  useEffect(() => {
    buscarTarefas();
  }, []);

  const renderColuna = (titulo, status) => (
    <div className="kanban-coluna">
      <h3>{titulo}</h3>
      {tarefas
        .filter((tarefa) => tarefa.status === status)
        .map((tarefa) => (
          <div className="kanban-card" key={tarefa._id}>
            <p>{tarefa.descricao}</p>
            <div className="acoes">
              {status !== 'concluido' && (
                <button
                  onClick={() =>
                    atualizarStatus(
                      tarefa._id,
                      status === 'pendente' ? 'andamento' : 'concluido'
                    )
                  }
                >
                  Avançar
                </button>
              )}
              <button onClick={() => deletarTarefa(tarefa._id)}>Excluir</button>
            </div>
          </div>
        ))}
    </div>
  );

  return (
    <div className="dashboard-kanban">
      <h2>Minhas Tarefas</h2>
      <div className="criar-tarefa">
        <input
          type="text"
          placeholder="Nova tarefa"
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
        />
        <button onClick={criarTarefa}>Criar</button>
      </div>
      <div className="kanban-board">
        {renderColuna('Pendente', 'pendente')}
        {renderColuna('Em Andamento', 'andamento')}
        {renderColuna('Concluído', 'concluido')}
      </div>
    </div>
  );
}

export default Dashboard;
