import { useEffect, useState } from 'react';
import api from '../services/api';
import '../styles/dashboard.css'; // pode ser reutilizado

function Backlog() {
  const [tarefas, setTarefas] = useState([]);
  const token = localStorage.getItem('token');

  const headers = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const carregarTarefas = async () => {
    try {
      const res = await api.get('/api/tarefas', headers);
      const pendentes = res.data.filter((tarefa) => tarefa.status === 'Pendente');
      setTarefas(pendentes);
    } catch (err) {
      console.error('Erro ao carregar backlog:', err);
    }
  };

  const excluirTarefa = async (id) => {
    try {
      await api.delete(`/api/tarefas/${id}`, headers);
      carregarTarefas();
    } catch (err) {
      console.error('Erro ao excluir tarefa:', err);
    }
  };

  const avancarTarefa = async (id) => {
    try {
      await api.put(`/api/tarefas/${id}`, { status: 'Em andamento' }, headers);
      carregarTarefas();
    } catch (err) {
      console.error('Erro ao mover para andamento:', err);
    }
  };

  useEffect(() => {
    carregarTarefas();
  }, []);

  return (
    <div className="dashboard-kanban">
      <h2>Gest√£o do Backlog</h2>
      {tarefas.length === 0 ? (
        <p>Sem tarefas pendentes.</p>
      ) : (
        tarefas.map((tarefa) => (
          <div className="kanban-card" key={tarefa._id}>
            <strong>{tarefa.nome}</strong>
            <p>{tarefa.descricao}</p>
            <div className="acoes">
              <button onClick={() => avancarTarefa(tarefa._id)}>Mover para "Em andamento"</button>
              <button onClick={() => excluirTarefa(tarefa._id)}>Excluir</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default Backlog;
