import { useEffect, useState } from 'react';
import api from '../services/api';
import '../styles/dashboard.css';

function Dashboard() {
  const [tarefas, setTarefas] = useState([]);
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');

  const fetchTarefas = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await api.get('/tarefas', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTarefas(res.data);
    } catch (error) {
      alert('Erro ao buscar tarefas');
    }
  };

  const handleCriarTarefa = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await api.post('/tarefas', { nome, descricao }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setNome('');
      setDescricao('');
      fetchTarefas();
    } catch (error) {
      alert('Erro ao criar tarefa');
    }
  };

  const avancarStatus = async (id, statusAtual) => {
    const statusOrdem = ['Pendente', 'Em andamento', 'Concluído'];
    const proximoIndex = statusOrdem.indexOf(statusAtual) + 1;
    if (proximoIndex >= statusOrdem.length) return;
    const novoStatus = statusOrdem[proximoIndex];
    try {
      const token = localStorage.getItem('token');
      await api.put(`/tarefas/${id}`, { status: novoStatus }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTarefas();
    } catch (error) {
      alert('Erro ao avançar tarefa');
    }
  };

  const excluirTarefa = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await api.delete(`/tarefas/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTarefas();
    } catch (error) {
      alert('Erro ao excluir tarefa');
    }
  };

  const renderColuna = (status, titulo) => (
    <div className="kanban-coluna">
      <h3>{titulo}</h3>
      {tarefas
        .filter((tarefa) => tarefa.status === status)
        .map((tarefa) => (
          <div className="kanban-tarefa" key={tarefa._id}>
            <strong>{tarefa.nome}</strong>
            <p>{tarefa.descricao}</p>
            {status !== 'Concluído' && (
              <button onClick={() => avancarStatus(tarefa._id, status)}>Avançar</button>
            )}
            <button onClick={() => excluirTarefa(tarefa._id)} className="excluir">Excluir</button>
          </div>
        ))}
    </div>
  );

  useEffect(() => {
    fetchTarefas();
  }, []);

  return (
    <div className="dashboard-container">
      <h2>Dashboard - Tarefas</h2>
      <form onSubmit={handleCriarTarefa} className="form-tarefa">
        <input
          type="text"
          placeholder="Nome da Tarefa"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <textarea
          placeholder="Descrição"
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
        />
        <button type="submit">Criar</button>
      </form>

      <div className="kanban-board">
        {renderColuna('Pendente', 'Pendente')}
        {renderColuna('Em andamento', 'Em andamento')}
        {renderColuna('Concluído', 'Concluído')}
      </div>
    </div>
  );
}

export default Dashboard;
